package com.syntax.class02;

public class ArithmetichOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// +, -,*,
		
		int num1=10;
		int num2=3;
		
		System.out.println(num1+num2);
		System.out.println(num1-num2);
		System.out.println(num1*num2);
		System.out.println(num1/num2);
		
		// int verilerinde küsürat sonuca yansımaz 
		
		// use double to see decimal 
		
		double num3=10;
		double num4=3;
		System.out.println(num3/num4);
		
		System.out.println(" ****** ");
		
		float num5=10f;
		float num6=3f;
		System.out.println(num5/num6);
		
		
		// % - remainder (kalan ı gösterim)
		
		int i=10;
		int j=7;
		
		System.out.println(i%j);
		
		int n1=20;
		int n2=5;
		
		int sum=n1+n2;
		int sub=n1-n2;
		int div=n1/n2;
		
		System.out.println(sum);
		System.out.println(sub);
		System.out.println(div);
		
		double d1=20;
		double d2=3;
		
		double division=d1/d2;
		
		System.out.println(division);
		
		// for see decimals in division use double
		
		

		
		
				

	}

}
