package com.syntax.class03;

public class Recap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String dayTime="morning";
		int date=22;
		String month="january";
		
		//Today is morning of january 22.
		
		//+  used to attach String to anything.
		
		System.out.println("Today is "+dayTime+" of "+month+" "+date +".");
		
		int year=2022;
		String year1="2022";
		
		System.out.println(year+10);// 2032 int+int
		
		System.out.println(year1+10);// 202210 string+int
		
		
	}

}
